import {useEffect} from 'react';
import './App.scss';
import Species from './Species';

const API_URL = 'https://swapi.dev/api/films/';

const parsingResponse = async response => {
  try {
    return response.json();
  } catch (error) {
    console.log('There was an error parsing the response', error);
    return undefined;
  }
};

const getFilm = async id => {
  if (!id) {
    return undefined;
  }

  try {
    const response = await fetch(`${API_URL}/${id}`);
    return parsingResponse();
    // return await response.json();
  } catch {
    console.log('There was an error fetching the film');
    return undefined;
  }
};

const getSpeciesFromFilm = async filmId => {
  if (!filmId) {
    return undefined;
  }

  const {species} = await getFilm(filmId);
  // species.forEach(specie => fetch(specie));
  const fetchSpecies = species.map(
    specie => new Promise(resolve => resolve(fetch(specie)))
  );

  try {
    const responses = await Promise.all(fetchSpecies);

    // build a function for getting the payload json() reuse on getFilm()
    const payloadResponses = responses.map(response => {
      return new Promise(resolve => resolve(response.json()));
    });
    //
    const pepe = await Promise.all(payloadResponses);
    console.log('pepe >> ', pepe);

    return payloadResponses;
  } catch (error) {
    console.log(
      "There were one or more than one species that coudn't be fetched"
    );
  }
};

const SPECIES_IMAGES = {
  droid:
    'https://static.wikia.nocookie.net/starwars/images/f/fb/Droid_Trio_TLJ_alt.png',
  human:
    'https://static.wikia.nocookie.net/starwars/images/3/3f/HumansInTheResistance-TROS.jpg',
  trandoshan:
    'https://static.wikia.nocookie.net/starwars/images/7/72/Bossk_full_body.png',
  wookie:
    'https://static.wikia.nocookie.net/starwars/images/1/1e/Chewbacca-Fathead.png',
  yoda: 'https://static.wikia.nocookie.net/starwars/images/d/d6/Yoda_SWSB.png',
};
const CM_TO_IN_CONVERSION_RATIO = 2.54;

const App = () => {
  useEffect(() => {
    getSpeciesFromFilm(2).then(() => console.log('species'));
  }, []);

  return (
    <div className="App">
      <h1>Empire Strikes Back - Species Listing ?</h1>
      <div className="App-species" />
    </div>
  );
};

export default App;
